import React, { useState } from 'react'
import axios from 'axios'
import './CreateStudent.css'
import { Navigate } from 'react-router-dom'

const CreateStudent = () => {
    const [name,setname]=useState('');
    const [mail,setmail]=useState('');

    function HandleSubmit(event){
        event.preventDefault();
        axios.post('http://localhost:8081/', {name,mail})
        .then(res=>{
            console.log(res)
        })
    }

  return (
  

    <div className='Main-Container-1'>
    <div className='First-Div-1'>
        <form onSubmit={HandleSubmit}>
        <h2>Add Student</h2>
        <div className='inner-div1'>
            <label htmlFor=''>Name</label>
            <input type='text' placeholder='Enter your Name' className='Form-Control' onChange={e=>setname(e.target.value)}/>
            </div>
            <div className='inner-div1'>
            <label htmlFor=''>Email</label>
            <input type='email' placeholder='Enter your Mail' className='Form-Control' onChange={e=>setmail(e.target.value)}/>
        </div>
        <button className='Butt-1'>Submit</button>
        </form>
       
    </div>
   </div>
  )
}

export default CreateStudent
